    response = requests.get('https://sandbox-healthservice.priaid.ch/symptoms?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..DmhLpExdxQTRT49VSjoEl1qLgB4Xia-E7mcmSXprKGU&language=en-gb')
