@api_view({'GET'})
def getIDSymptoms(request, symptom):
    if request.method == 'GET':
        for symptom in requests.get('https://sandbox-healthservice.priaid.ch/symptoms?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..DmhLpExdxQTRT49VSjoEl1qLgB4Xia-E7mcmSXprKGU&language=en-gb'):
            if symptom['Name'] == symptom:
                return Response(symptom)

@api_view(["POST"])
def addSymptoms(request):
    serializer = SymptomSerializer(data=request.data)
    if serializer.is_valid():
        serializer.save()
    return Response(serializer.data)